// n8n Code Node - Parse JSON from LLM Response
// This handles responses that contain JSON wrapped in markdown code blocks
const inputData = $input.all();
let results = [];
let logs =  [];

let data = null;
let shouldStop  = false;
for (let i = 0; i < inputData.length; i++) {
  const item = inputData[i];

  // Get the LLM response text (adjust the path based on your LLM node output)
  let responseText = item.json.response || item.json.text || item.json.content || item.json.output;

  // If the response is nested deeper, try common paths
  if (!responseText && item.json.choices && item.json.choices[0]) {
    responseText = item.json.choices[0].message?.content || item.json.choices[0].text;
  }

  // Handle Gemini API response structure
  if (!responseText && item.json.candidates && item.json.candidates[0]) {
    responseText = item.json.candidates[0].content?.parts?.[0]?.text;
  }

  if (!responseText) {
    // Fallback: try to find any string value that looks like JSON
    const jsonString = JSON.stringify(item.json);
    const jsonMatch = jsonString.match(/```json\n([\s\S]*?)\n```/);
    if (jsonMatch) {
      responseText = jsonMatch[1];
    } else {
     shouldStop= true;
        logs.push('Something went wrong while parsing the AI response to add data to       Unleashed Software.');
        results.push({
      json: {
        order_data : null,
        logs,
        from_email :$('Loop Emails').first().json.from,
        email_id: $('Loop Emails').first().json.conversationId,
        _processing_timestamp: new Date().toISOString(),
        file_name:$('Loop Attachments').first().binary.data.fileName,
