        status:"error",
        purchase_order_guid:null,
        purchase_created: false,
        purchase_supabase_id:null,
        order_status:null
      }
    });

    }
  }

  if(!shouldStop){
     try {
    // Extract JSON from markdown code blocks
    let jsonString = responseText;
    // Remove markdown code block formatting if present
    const codeBlockMatch = responseText.match(/```json\n([\s\S]*?)\n```/);
    if (codeBlockMatch) {
      jsonString = codeBlockMatch[1];
    } else {
      // Try to find JSON without code blocks
      const jsonMatch = responseText.match(/\{[\s\S]*\}/);
      if (jsonMatch) {
        jsonString = jsonMatch[0];
      }
    }

    // Clean up the JSON string
    jsonString = jsonString.trim();

    // Parse the JSON
    const parsedJson = JSON.parse(jsonString);

    /*********************** Harde Code *********************/
    parsedJson.Customer.CustomerCode="123";
    // const taxRate = parsedJson?.TaxRate ??  0.10;
      const taxRate =  0.10;
    /********************************************************/
    if(parsedJson.errors.length){
      logs = parsedJson.errors;
    }

    if(!parsedJson?.OrderStatus){
       parsedJson.OrderStatus = 'Parked';
    }

    // Validate and process SalesOrderLines
    if(Array.isArray(parsedJson?.SalesOrderLines)){
        parsedJson.SalesOrderLines.forEach((line) => {
     if (!line?.LineTotal || line?.LineTotal <= 0) {
      line.LineTotal = line.OrderQuantity * line.UnitPrice;
      // Assuming 'line.productCode' exists
      logs.push(`LineTotal for product ${line.Product.ProductCode}: Manually calculated, not found.`); 
    }


    if (!line?.LineTax || line?.LineTax <=  0) {

      line.LineTax = parseFloat((line.LineTotal * taxRate).toFixed(2));
      // Assuming 'line.productCode' exists
      logs.push(`LineTax for product ${line.Product.ProductCode}: Manually calculated, not found.`);
    }
          });
        }

   if (!parsedJson?.SubTotal || parsedJson?.SubTotal == 0) {
      parsedJson.SubTotal = parseFloat(parsedJson.SalesOrderLines.reduce((sum, line) => sum + line.LineTotal, 0).toFixed(2));
      logs.push("SubTotal: Manually calculated, not found.");
  }

  if (!parsedJson?.TaxTotal || parsedJson?.TaxTotal <=  0) {
    parsedJson.TaxTotal = parseFloat(parsedJson.SalesOrderLines.reduce((sum, line) => sum + line.LineTax, 0).toFixed(2));
    logs.push("TaxTotal: Manually calculated, not found.");
  }

  if (!parsedJson?.Total || parsedJson?.Total <=  0) {
    parsedJson.Total = parseFloat((parsedJson.SubTotal + parsedJson.TaxTotal).toFixed(2));
    logs.push("Total: Manually calculated, not found.");
  }

    delete  parsedJson.errors;
    parsedJson.OrderNumber = Math.floor(10000 + Math.random() * 90000).toString();
    data = parsedJson;
    // Return the parsed JSON object
    results.push({
      json: {
