  }
  }

}

return results;

const rawMessage = $input.item.json.error?.message || '';
let errors = [];

try {
  // Step 1: Remove HTTP status prefix like "400 - "
  let jsonPart = rawMessage.replace(/^\d+ - /, '').trim();

  // Step 2: Remove outer quotes if present
  if (jsonPart.startsWith('"') && jsonPart.endsWith('"')) {
    jsonPart = jsonPart.slice(1, -1);
  }

  // Step 3: Clean escape characters
  jsonPart = jsonPart
    .replace(/\\"/g, '"')            // Escaped quotes
    .replace(/\\n/g, '')             // Newlines
    .replace(/\\u0027/g, "'")        // Unicode apostrophe
    .replace(/\\u0022/g, '"')        // Unicode quote
    .replace(/\\u([0-9a-fA-F]{4})/g, (_, code) => String.fromCharCode(parseInt(code, 16)))
    .replace(/\\\\/g, '\\')          // Fix double backslashes
    .replace(/\\([^u])/g, '$1')      // Remove stray backslashes
    .trim();

  // Debug print if needed
  // console.log('Cleaned JSON string:', jsonPart);

  // Step 4: Try parsing the JSON string
  const parsed = JSON.parse(jsonPart);

  // Step 5: Handle known error formats

  // Case: Array of Items with error details
  if (Array.isArray(parsed.Items)) {
    parsed.Items.forEach(item => {
      errors.push({
        field: item.Field || null,
        message: item.Description || 'Unknown error',
        code: item.ErrorCode ?? null
      });
    });
  }

  // Case: Flat single error with Description
  else if (parsed.Description) {
    errors.push({
      field: null,
      message: parsed.Description,
      code: parsed.ErrorCode ?? null
    });
  }

  // Case: Fallback ‚Äî try to extract string properties
  else {
    const extracted = Object.entries(parsed).filter(([_, v]) => typeof v === 'string');
    if (extracted.length) {
      extracted.forEach(([key, val]) => {
        errors.push({
          field: key,
          message: val,
          code: null
        });
      });
    } else {
      errors.push({
        field: null,
        message: 'Unrecognized error structure',
        code: null
      });
    }
  }

} catch (err) {
  errors.push({
    field: null,
    message: 'Failed to parse error message: ' + err.message,
    code: null
  });
}

// Final output as array of n8n-style JSON items
return errors.map(error => ({ json: error }));

